use num_bigint::BigUint;
use risc0_bigint2_methods::RSA_ELF;
use risc0_zkvm::{ExecutorEnv, ExitCode, ProverOpts, get_prover_server};
use rstest::rstest;

use crate::BigUintWrap;

#[rstest]
#[case(
    "",
    "",
    ""
)]
#[case(
    "1",
    "",
    "1"
)]
#[case(
    "",
    "7fffffff",
    "47d8adb9"
)]
#[should_panic(expected = "Input too large")]
#[case(
    "",
    "2",
    "0"
)]
#[should_panic(expected = "Input too large")]
#[case(
    "1",
    "",
    "0"
)]
#[test_log::test]
#[gpu_guard::gpu_guard]
fn modpow_65537(
    #[case] base: BigUintWrap,
    #[case] modulus: BigUintWrap,
    #[case] expected: BigUintWrap,
) {
    let env = ExecutorEnv::builder()
        .write(&(base.0, modulus.0))
        .unwrap()
        .build()
        .unwrap();
    let opts = ProverOpts::fast();
    let receipt = get_prover_server(&opts)
        .unwrap()
        .prove(env, RSA_ELF)
        .unwrap()
        .receipt;
    assert_eq!(
        receipt.claim().unwrap().as_value().unwrap().exit_code,
        ExitCode::Halted(0)
    );
    let result: BigUint = receipt.journal.decode().unwrap();
    assert_eq!(result, expected.0);
}
